  do
    Keyword.validate!(opts || [], [:oriented])
    positional = [
      contour: Evision.Internal.Structurise.from_struct(contour)
    ]
    :evision_nif.contourArea(positional ++ Evision.Internal.Structurise.from_struct(opts || []))
     |> to_struct()
  end

