  do
    positional = [
      src: Evision.Internal.Structurise.from_struct(src),
      flipCode: Evision.Internal.Structurise.from_struct(flipCode)
    ]
    :evision_nif.flip(positional ++ Evision.Internal.Structurise.from_struct(opts || []))
     |> to_struct()
  end

